<?phpfunction array_sort_by_column(&$arr, $col, $dir = SORT_ASC) {    $sort_col = array();    foreach ($arr as $key=> $row) {        $sort_col[$key] = $row[$col];    }    array_multisort($sort_col, $dir, $arr);}function sortArray($array, $key_s,$desc=false) { 	if($desc)	{		array_sort_by_column($array, $key_s,SORT_DESC);	}	else	{		array_sort_by_column($array, $key_s);	}	return $array;} ?>